mapper.select:
select a.reg_seq regSeq, a.emg_seq emgSeq, a.vst_cad vstCad, a.id_nbr idNbr, a.cst_nam cstNam, a.cst_sex_cod cstSexCod, (case when a.cst_age_cod = '0' then a.cst_age else '0' end) cstAge, (select pro_val from hsp_czzl_inf where hsp_czzl_inf.pro_code = 'FBSJ' and hsp_czzl_inf.emg_no = emg_seq) fbsj, (select pro_val from hsp_czzl_inf where hsp_czzl_inf.pro_code = 'CBZD' and hsp_czzl_inf.emg_no = emg_seq) cbzd, (select pro_val from hsp_czzl_inf where hsp_czzl_inf.pro_code = 'ZDYS' and hsp_czzl_inf.emg_no = emg_seq) zdys, a.way_typ wayTyp, a.crt_tim crtTim, a.smt_seq smtSeq, a.smt_sta smtSta from hsp_dbzl_bas a where a.PAT_TYP = '2' and a.hsp_ara =#{hspAra,jdbcType=VARCHAR}
and a.cst_nam like '%'||#{cstNam, jdbcType = VARCHAR}||'%'
and exists (select 1 from hsp_czzl_inf where hsp_czzl_inf.emg_no = a.emg_seq and hsp_czzl_inf.pro_code = 'CBZD' and hsp_czzl_inf.pro_val = #{zd, jdbcType = VARCHAR})
and exists (select 1 from hsp_czzl_inf where hsp_czzl_inf.emg_no = a.emg_seq and hsp_czzl_inf.pro_code = 'ZDYS' and hsp_czzl_inf.pro_val like '%'||#{zdys, jdbcType = VARCHAR}||'%')
and a.crt_tim > #{startDate, jdbcType = TIMESTAMP} and a.crt_tim < #{endDate, jdbcType = TIMESTAMP}
