mapper.select:
select 

from HSP_CEREBRAL_SHEET
where CEREBRAL_COURSE_SEQ = #{cerebralCourseSeq,jdbcType=VARCHAR}
  and TIME_TYPE = #{timeType,jdbcType=DECIMAL}

mapper.delete:
delete from HSP_CEREBRAL_SHEET
where CEREBRAL_COURSE_SEQ = #{cerebralCourseSeq,jdbcType=VARCHAR}
  and TIME_TYPE = #{timeType,jdbcType=DECIMAL}
